// @ts-nocheck
"use client"

import React, { useEffect, useRef } from 'react'
import L from 'leaflet'
import 'leaflet/dist/leaflet.css'
import 'leaflet-routing-machine/dist/leaflet-routing-machine.css'
import 'leaflet-routing-machine'

// Extended type declarations for Leaflet
declare global {
  namespace L {
    interface LatLng {
      lat: number;
      lng: number;
    }

    interface IconOptions extends L.BaseIconOptions {
      iconUrl: string;
      iconSize?: L.PointExpression;
      iconAnchor?: L.PointExpression;
      popupAnchor?: L.PointExpression;
      shadowUrl?: string;
      shadowSize?: L.PointExpression;
      shadowAnchor?: L.PointExpression;
    }

    class Icon extends L.Layer {
      constructor(options: IconOptions);
    }

    namespace Routing {
      function control(options: ControlOptions): Control;
      interface Control extends L.Control {
        addTo(map: L.Map): this;
      }
      interface ControlOptions {
        waypoints: L.LatLng[];
        routeWhileDragging?: boolean;
        lineOptions?: {
          styles?: {
            color: string;
            weight: number;
          }[];
          extendToWaypoints?: boolean;
          missingRouteTolerance?: number;
        };
        show?: boolean;
        addWaypoints?: boolean;
        draggableWaypoints?: boolean;
        fitSelectedRoutes?: boolean;
      }
    }
  }
}

// Custom icon for markers
const customIcon = new L.Icon({
  iconUrl: 'data:image/png;base64,',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowUrl: 'data:image/png;base64,',
  shadowSize: [41, 41],
  shadowAnchor: [12, 41]
});

interface MapProps {
  center: [number, number];
  destination?: [number, number];
}

const Map: React.FC<MapProps> = ({ center, destination }) => {
  const mapRef = useRef<L.Map | null>(null);
  const routingControlRef = useRef<L.Routing.Control | null>(null);

  useEffect(() => {
    if (!mapRef.current) {
      mapRef.current = L.map('map', {
        zoomControl: false,
        attributionControl: false
      }).setView(center, 13);

      L.tileLayer('https://{s}.google.com/vt/lyrs=m&x={x}&y={y}&z={z}', {
        maxZoom: 20,
        subdomains: ['mt0', 'mt1', 'mt2', 'mt3']
      }).addTo(mapRef.current);

      new L.Marker(center, { icon: customIcon }).addTo(mapRef.current);
    } else {
      mapRef.current.setView(center);
    }

    if (destination) {
      new L.Marker(destination, { icon: customIcon }).addTo(mapRef.current!);

      if (routingControlRef.current) {
        mapRef.current!.removeControl(routingControlRef.current);
      }

      routingControlRef.current = L.Routing.control({
        waypoints: [
          new L.LatLng(center[0], center[1]),
          new L.LatLng(destination[0], destination[1])
        ],
        routeWhileDragging: true,
        lineOptions: {
          styles: [{ color: '#4285F4', weight: 6 }],
          extendToWaypoints: true,
          missingRouteTolerance: 0
        },
        show: false,
        addWaypoints: false,
        draggableWaypoints: false,
        fitSelectedRoutes: true
      }).addTo(mapRef.current!);
    }

    return () => {
      if (mapRef.current) {
        mapRef.current.remove();
        mapRef.current = null;
      }
      if (routingControlRef.current) {
        routingControlRef.current = null;
      }
    }
  }, [center, destination]);

  return <div id="map" className="map-container" />;
};

export default Map;